sp_widget.client_script:
api.controller=function($scope) {
	var c = this;
	c.qrCode = '';

	// Initialize the QuaggaJS barcode scanner
	Quagga.init({
		inputStream: {
			name: "Live",
			type: "LiveStream",
			target: document.querySelector('#qr-scanner-container'),
			constraints: {
				width: 640,
				height: 480,
				facingMode: "environment" // Use the back camera
			}
		},
		decoder: {
			readers: [] // Disable all readers, since we'll use jsQR for QR code scanning
		}
	}, function (err) {
		if (err) {
			console.error(err);
			return;
		}
		console.log("QR code scanner initialized successfully.");
		Quagga.start();
	});

	Quagga.onProcessed(function (result) {
		if (result && result.boxes) {
			// Grab the live video frame and extract the image data
			var video = document.querySelector('video');
			if (video) {
				var canvas = document.createElement('canvas');
				var context = canvas.getContext('2d');
				canvas.width = video.videoWidth;
				canvas.height = video.videoHeight;
				context.drawImage(video, 0, 0, canvas.width, canvas.height);

				var imageData = context.getImageData(0, 0, canvas.width, canvas.height);
				var qrCode = jsQR(imageData.data, canvas.width, canvas.height);

				if (qrCode) {
					$scope.$apply(function () {
						c.qrCode = qrCode.data;
					});
					console.log("QR Code detected: " + qrCode.data);
				}
			}
		}
	});
	$scope.$on('$destroy', function () {
		Quagga.stop();
	});

};
