sp_widget.client_script:
api.controller=function($scope, $timeout) {
  /* widget controller */
  var c = this;
	$scope.signatureData = '';

  var signaturePad;
  var canvas;

  // Initialize the signature pad after the DOM is ready
  $timeout(function() {
    canvas = document.getElementById('signature-pad');
    signaturePad = new SignaturePad(canvas);

    // Function to resize the canvas
    function resizeCanvas() {
      var ratio = Math.max(window.devicePixelRatio || 1, 1);
      canvas.width = canvas.offsetWidth * ratio;
      canvas.height = canvas.offsetHeight * ratio;
      canvas.getContext('2d').scale(ratio, ratio);
      signaturePad.clear(); // Clear the canvas after resizing
    }

    // Resize canvas initially and on window resize
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
  }, 0);

  // Clear the signature
  $scope.clearSignature = function() {
    if (signaturePad) {
      signaturePad.clear();
      $scope.signatureData = '';
    }
  };

  // Save the signature as a data URL
  $scope.saveSignature = function() {
    if (signaturePad && !signaturePad.isEmpty()) {
      $scope.signatureData = signaturePad.toDataURL();
    } else {
      alert('Please provide a signature first.');
    }
  };

  // Clean up when the widget is destroyed to avoid memory leaks
  $scope.$on('$destroy', function() {
    window.removeEventListener('resize', resizeCanvas);
  });
};
