sp_widget.script:
(function() {
	if (input && input.searchTerm) {
		var gr = new GlideRecord('cmn_location'); 
		gr.addQuery('name', 'CONTAINS', input.searchTerm);
		gr.setLimit(5);
		gr.query();
		var results = [];
		if (input.action == 'getLocations') {
			while (gr.next()) {
				results.push({
					sys_id: gr.sys_id.toString(),
					name: gr.name.toString()   
				});
			}
			data.results = results;
		}
		data.results = results;
	}
})();
